import React, { useState } from "react";
import "./Login.css";
import { Link } from "react-router-dom";

function Login() {
  const [role, setRole] = useState("jobseeker");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();
    console.log("Login payload:", { role, email, password });
    alert("Login payload logged to console. See devtools.");
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2 className="login-title">WorkConnect</h2>
        <p className="login-subtitle">Sign in to your account</p>

        <div className="role-toggle">
          <label className={role === "jobseeker" ? "active" : ""}>
            <input
              type="radio"
              name="role"
              value="jobseeker"
              checked={role === "jobseeker"}
              onChange={() => setRole("jobseeker")}
            />
            Jobseeker
          </label>
          <label className={role === "employer" ? "active" : ""}>
            <input
              type="radio"
              name="role"
              value="employer"
              checked={role === "employer"}
              onChange={() => setRole("employer")}
            />
            Employer
          </label>
        </div>

        <form onSubmit={onSubmit}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            className="input-field"
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            className="input-field"
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="login-btn">
            Login as {role}
          </button>
        </form>

        <div className="login-links">
          <a href="#">Forgot password?</a>
          <Link to="/signup-01">Create account</Link>
        </div>
      </div>
    </div>
  );
}

export default Login;
