import React, { useState } from "react";

function Register() {
  const [role, setRole] = useState("jobseeker");
  const [form, setForm] = useState({
    fullName: "",
    email: "",
    password: "",
    company: "",
    companyWebsite: "",
    headline: "",
    skills: "",
  });

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  const onSubmit = (e) => {
    e.preventDefault();
    const payload = { role, ...form };
    // For now just log the payload. Replace with API call.
    console.log("Register payload:", payload);
    alert("Register payload logged to console. See devtools.");
  };

  return (
    <div>
      <h2>Register</h2>

      <div style={{ marginBottom: 12 }}>
        <label style={{ marginRight: 8 }}>
          <input
            type="radio"
            name="role"
            value="jobseeker"
            checked={role === "jobseeker"}
            onChange={() => setRole("jobseeker")}
          />
          Jobseeker / Employee
        </label>
        <label>
          <input
            type="radio"
            name="role"
            value="employer"
            checked={role === "employer"}
            onChange={() => setRole("employer")}
          />
          Employer
        </label>
      </div>

      <form onSubmit={onSubmit}>
        <input
          name="fullName"
          value={form.fullName}
          onChange={onChange}
          type="text"
          placeholder="Full Name"
        />
        <br />

        <input
          name="email"
          value={form.email}
          onChange={onChange}
          type="email"
          placeholder="Email"
        />
        <br />

        <input
          name="password"
          value={form.password}
          onChange={onChange}
          type="password"
          placeholder="Password"
        />
        <br />

        {role === "employer" ? (
          <div>
            <h4>Employer details</h4>
            <input
              name="company"
              value={form.company}
              onChange={onChange}
              type="text"
              placeholder="Company name"
            />
            <br />
            <input
              name="companyWebsite"
              value={form.companyWebsite}
              onChange={onChange}
              type="text"
              placeholder="Company website"
            />
            <br />
          </div>
        ) : (
          <div>
            <h4>Jobseeker details</h4>
            <input
              name="headline"
              value={form.headline}
              onChange={onChange}
              type="text"
              placeholder="Professional headline (e.g. Frontend Developer)"
            />
            <br />
            <input
              name="skills"
              value={form.skills}
              onChange={onChange}
              type="text"
              placeholder="Comma-separated skills"
            />
            <br />
          </div>
        )}

        <button type="submit">Register as {role}</button>
      </form>
    </div>
  );
}

export default Register;
