import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function SignUp01() {
  const [role, setRole] = useState("jobseeker");
  const [form, setForm] = useState({ fullName: "", email: "", password: "", company: "" });
  const navigate = useNavigate();

  const onChange = (e) => setForm((f) => ({ ...f, [e.target.name]: e.target.value }));

  const onSubmit = (e) => {
    e.preventDefault();
    const payload = { role, ...form };
    console.log("SignUp01 payload:", payload);
    // placeholder behavior: navigate to jobs after signup
    navigate("/jobs");
  };

  return (
    <div style={{ maxWidth: 540, margin: "24px auto", padding: 24, border: "1px solid #eee", borderRadius: 8 }}>
      <h2>Create your account</h2>
      <p>Sign up as an Employer or Jobseeker</p>

      <div style={{ marginBottom: 12 }}>
        <label style={{ marginRight: 12 }}>
          <input type="radio" checked={role === "jobseeker"} onChange={() => setRole("jobseeker")} /> Jobseeker
        </label>
        <label>
          <input type="radio" checked={role === "employer"} onChange={() => setRole("employer")} /> Employer
        </label>
      </div>

      <form onSubmit={onSubmit}>
        <div style={{ marginBottom: 8 }}>
          <input name="fullName" value={form.fullName} onChange={onChange} placeholder="Full name" style={{ width: "100%", padding: 8 }} />
        </div>
        <div style={{ marginBottom: 8 }}>
          <input name="email" value={form.email} onChange={onChange} placeholder="Email" type="email" style={{ width: "100%", padding: 8 }} />
        </div>
        <div style={{ marginBottom: 8 }}>
          <input name="password" value={form.password} onChange={onChange} placeholder="Password" type="password" style={{ width: "100%", padding: 8 }} />
        </div>

        {role === "employer" && (
          <div style={{ marginBottom: 8 }}>
            <input name="company" value={form.company} onChange={onChange} placeholder="Company name" style={{ width: "100%", padding: 8 }} />
          </div>
        )}

        <div style={{ marginTop: 12 }}>
          <button type="submit" style={{ padding: "8px 16px" }}>Create account</button>
        </div>
      </form>
    </div>
  );
}

export default SignUp01;
