import React, { createContext, useContext, useEffect, useState } from 'react';

const JobsContext = createContext(null);

export function JobsProvider({ children }) {
  const defaultJobs = [
    {
      id: 1,
      title: 'Product Designer',
      company: 'Acme Company',
      location: 'Remote',
      type: 'Full-time',
      salary: '$40k - $60k',
      summary: 'Design delightful product experiences.',
      description: 'Work with cross-functional teams to design user experiences.',
      easyApply: true,
      isRemote: true,
      isHybrid: false,
      isFullTime: true,
      postedAt: '2025-10-10',
      applied: false,
      exclusive: false,
    },
    {
      id: 2,
      title: 'UI/UX Designer',
      company: 'DesignCo',
      location: 'Lisbon, PT',
      type: 'Contract',
      salary: '$30/hr',
      summary: 'Create UI mockups and prototypes.',
      description: 'Collaborate with product team to deliver high-fidelity designs.',
      easyApply: false,
      isRemote: false,
      isHybrid: false,
      isFullTime: false,
      postedAt: '2025-10-12',
      applied: true,
      exclusive: true,
    },
    {
      id: 3,
      title: 'Frontend Engineer',
      company: 'Webify',
      location: 'Manila, PH',
      type: 'Full-time',
      salary: '$20k - $35k',
      summary: 'Implement UI using React.',
      description: 'Develop and maintain frontend components and pages.',
      easyApply: true,
      isRemote: false,
      isHybrid: false,
      isFullTime: true,
      postedAt: '2025-09-30',
      applied: false,
      exclusive: false,
    }
  ];

  const [jobs, setJobs] = useState(() => {
    try {
      const raw = localStorage.getItem('wc_jobs');
      return raw ? JSON.parse(raw) : defaultJobs;
    } catch (e) {
      return defaultJobs;
    }
  });

  useEffect(() => {
    try {
      localStorage.setItem('wc_jobs', JSON.stringify(jobs));
    } catch (e) {
      // ignore storage errors
    }
  }, [jobs]);

  function addJob(job) {
    const newJob = {
      id: Date.now(),
      title: job.title || 'Untitled',
      company: job.company || 'Unknown',
      location: job.location || '',
      type: job.type || '',
      salary: job.salary || '',
      summary: job.summary || job.description?.slice(0, 120) || '',
      description: job.description || job.summary || '',
      easyApply: !!job.easyApply,
      isRemote: job.workArrangement === 'remote' || false,
      isHybrid: job.workArrangement === 'hybrid' || false,
      isFullTime: job.type && job.type.toLowerCase().includes('full'),
      postedAt: new Date().toISOString().slice(0, 10),
      applied: false,
      exclusive: !!job.exclusive,
    };
    setJobs(prev => [newJob, ...prev]);
  }

  return (
    <JobsContext.Provider value={{ jobs, setJobs, addJob }}>
      {children}
    </JobsContext.Provider>
  );
}

export function useJobs() {
  const ctx = useContext(JobsContext);
  if (!ctx) throw new Error('useJobs must be used within JobsProvider');
  return ctx;
}
