import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useSignup } from "../../../contexts/SignupContext";
import "../../signup/signup.css"; // go up one level then into signup folder
import SignupProgress from "../../signup/SignupProgress";
import CountrySelect from "../../signup/CountrySelect";
import { validatePhone } from "../../../utils/phone";
import { BsEye, BsEyeSlash } from "react-icons/bs";


function SignUp01() {
  const { data, update, setCurrentStep } = useSignup();
  const navigate = useNavigate();

  // state for phone error display
  const [phoneError, setPhoneError] = useState("");
  // state for toggling password visibility
  const [showPassword, setShowPassword] = useState(false);

  // handle input changes
  const onChange = (e) => update({ [e.target.name]: e.target.value });

  // handle form submission
  const onSubmit = async (e) => {
    e.preventDefault();

    // basic email + password validation
    if (!data.email || !data.password) {
      alert('Email and password are required');
      return;
    }
    if (data.password.length < 8 || !/[0-9]/.test(data.password) || !/[A-Za-z]/.test(data.password)) {
      alert('Password must be at least 8 characters and include letters and numbers');
      return;
    }
    if (data.password !== data.confirmPassword) {
      alert('Passwords do not match');
      return;
    }

    // proceed to personal info step
    navigate('/signup-02');
  };

  // mark current step for progress bar
  useEffect(() => setCurrentStep(1), [setCurrentStep]);

  return (
    <div className="signup01-container">
      {/* top progress indicator */}
      <SignupProgress currentStep={1} />

      <h1 className="signup01-title">Give Us Your Primary Information</h1>

      {/* role selection */}
      <div style={{ marginBottom: 12 }}>
        <label style={{ marginRight: 12 }}>
          <input
            type="radio"
            name="role"
            value="jobhunter"
            checked={data.role === "jobhunter"}
            onChange={(e) => update({ role: e.target.value })}
          />{" "}
          Job Hunter / Employee
        </label>
        <label>
          <input
            type="radio"
            name="role"
            value="employer"
            checked={data.role === "employer"}
            onChange={(e) => update({ role: e.target.value })}
          />{" "}
          Employer
        </label>
      </div>

      {/* form fields */}
      <form onSubmit={onSubmit}>
        <div className="signup01-grid">
          <div>
            <label className="signup01-label">Name</label>
            <input
              className="signup01-input"
              name="firstName"
              value={data.firstName}
              onChange={onChange}
              placeholder="Write Your Name"
            />
          </div>

          <div>
            <label className="signup01-label">Last Name</label>
            <input
              className="signup01-input"
              name="lastName"
              value={data.lastName}
              onChange={onChange}
              placeholder="Write Your Last Name"
            />
          </div>

          <div>
            <label className="signup01-label">Email*</label>
            <input
              className="signup01-input"
              name="email"
              value={data.email}
              onChange={onChange}
              placeholder="example@email.com"
            />
          </div>

          {/* password field with show/hide toggle */}
          <div>
  <label className="signup01-label" style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
    <span>Password*</span>
    <span
      className="eye-toggle-label"
      onClick={() => setShowPassword(!showPassword)}
      title={showPassword ? "Hide password" : "Show password"}
    >
      {showPassword ? <BsEyeSlash /> : <BsEye />}
    </span>
  </label>

  <input
    className="signup01-input"
    name="password"
    value={data.password}
    onChange={onChange}
    type={showPassword ? "text" : "password"}
    placeholder="Create a password"
  />
</div>

          <div>
            <label className="signup01-label">Confirm Password*</label>
            <input
              className="signup01-input"
              name="confirmPassword"
              value={data.confirmPassword || ''}
              onChange={onChange}
              type={showPassword ? 'text' : 'password'}
              placeholder="Confirm your password"
            />
          </div>

        </div>

        {/* phone number section */}
        <div className="signup01-grid" style={{ marginTop: 12 }}>
          <div>
            <label className="signup01-label">Phone Number*</label>
            <div style={{ display: "flex", gap: 8 }}>
              <div
                className={phoneError ? "invalid-box" : ""}
                style={{
                  display: "flex",
                  alignItems: "center",
                  padding: "8px 10px",
                  border: "1px solid #eee",
                  borderRadius: 6,
                  background: "#fff",
                }}
              >
                <div style={{ width: 320 }}>
                  <CountrySelect
                    value={data.phoneCountry || "+63"}
                    onChange={(code) => {
                      setPhoneError("");
                      update({ phoneCountry: code });
                    }}
                  />
                </div>
              </div>
              <input
                className={`signup01-input ${
                  phoneError ? "invalid-input" : ""
                }`}
                name="phone"
                value={data.phone}
                onChange={(e) => {
                  setPhoneError("");
                  onChange(e);
                }}
                placeholder="712345678"
                style={{ flex: 1 }}
              />
            </div>
            {phoneError && <div className="signup-error">{phoneError}</div>}
          </div>
        </div>

        <button className="signup01-continue" type="submit">
          Continue
        </button>
      </form>
    </div>
  );
}

export default SignUp01;
