import React from 'react';
import './Slideshow.css';

// Simple configurable slideshow that animates slides from right to left
function Slideshow({ slides = [], duration = 8000 }) {
  if (!slides || slides.length === 0) return null;

  // duplicate slides to create a seamless loop
  const doubled = [...slides, ...slides];

  return (
    <div className="slideshow-wrapper">
      <div className="slideshow-track" style={{ animationDuration: `${duration}ms` }}>
        {doubled.map((s, i) => (
          <div className="slide" key={i} aria-hidden={i >= slides.length}>
            {typeof s === 'string' ? <div className="slide-content">{s}</div> : s}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Slideshow;
