import React, { useState } from "react";
import { Link } from "react-router-dom";
import { login } from "../../api/auth";
import "./Login.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const onSubmit = async (e) => {
  e.preventDefault();

  try {
    const result = await login({ email, password }); // calls backend

    console.log("Login successful:", result);

    // Store token for later authenticated requests
    localStorage.setItem("token", result.token);
    localStorage.setItem("userId", result.userId);

    // Redirect after login
    window.location.href = "/page/Jobs.js"; // or /jobs, /profile, etc.
  } catch (err) {
    console.error("Login failed:", err);
    alert("Invalid email or password. Please try again.");
  }
};

  return (
    <div className="login-page">
      {/* Left side (brand/description) */}
      <div className="login-left">
        <h1 className="brand-title">WorkConnect</h1>
        <p className="brand-subtitle">
          Connect with employers, find jobs, and grow your career.
        </p>
      </div>

      {/* Right side (login form) */}
      <div className="login-right">
        <div className="login-box">
          <h2>Sign in</h2>
          <p className="login-desc">
            Stay updated on your professional world
          </p>

          <form onSubmit={onSubmit}>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button type="submit" className="signin-btn">
              Sign in
            </button>
          </form>

          <div className="links">
            <a href="#" className="forgot-link">
              Forgot password?
            </a>
            <p>
              New to WorkConnect?{" "}
              <Link to="/choose-role" className="join-link">
                Join now
              </Link>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
