import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useSignup } from "../../../contexts/SignupContext";
import "../signup.css";
import SignupProgress from "../SignupProgress";
import { useEffect } from "react";

function SignUp02() {
  const navigate = useNavigate();
  const { data, update, setCurrentStep } = useSignup();
  useEffect(() => setCurrentStep(2), [setCurrentStep]);
  const [skills, setSkills] = useState((data.skills || []).join(', '));
  const [resumeFile, setResumeFile] = useState(null);
  const [address] = useState(data.address || '');
  const onContinue = () => {
    // validate required fields
    if (!data.firstName || !data.lastName) {
      alert('Please enter your first and last name');
      return;
    }
    // normalize phone
    navigate('/signup-03');
  };

  return (
    <div className="signup01-container">
      <SignupProgress currentStep={2} />
      <h1 className="signup01-title">Profile details</h1>
      <p>Current role: <strong>{data.role}</strong></p>

      <div style={{ marginTop: 12 }}>
        <label>City</label>
        <br />
        <input style={{ width: 300 }} name="city" value={data.city || ''} onChange={e => update({ city: e.target.value })} placeholder="City" />
      </div>

      <div style={{ marginTop: 12 }}>
        <label>Region</label>
        <br />
        <input style={{ width: 300 }} name="region" value={data.region || ''} onChange={e => update({ region: e.target.value })} placeholder="Region / State" />
      </div>

      <div style={{ marginTop: 12 }}>
        <label>Date of birth (optional)</label>
        <br />
        <input type="date" name="dateOfBirth" value={data.dateOfBirth || ''} onChange={e => update({ dateOfBirth: e.target.value })} />
      </div>

      <div style={{ marginTop: 12 }}>
        <label>Gender (optional)</label>
        <br />
        <select name="gender" value={data.gender || ''} onChange={e => update({ gender: e.target.value })}>
          <option value="">Prefer not to say</option>
          <option value="female">Female</option>
          <option value="male">Male</option>
          <option value="other">Other</option>
        </select>
      </div>

      <div style={{ marginTop: 18 }}>
        <button className="signup01-continue" onClick={() => navigate('/signup-01')} style={{ marginRight: 8 }}>Back</button>
        <button className="signup01-continue" onClick={onContinue}>Continue</button>
      </div>
    </div>
  );
}

export default SignUp02;
