import React, { useState, useRef, useEffect } from 'react';

const COUNTRIES = [
  { code: '+63', name: 'Philippines', flag: 'ðŸ‡µðŸ‡­' },
  { code: '+1', name: 'United States', flag: 'ðŸ‡ºðŸ‡¸' },
  { code: '+44', name: 'United Kingdom', flag: 'ðŸ‡¬ðŸ‡§' },
  { code: '+61', name: 'Australia', flag: 'ðŸ‡¦ðŸ‡º' },
  { code: '+91', name: 'India', flag: 'ðŸ‡®ðŸ‡³' },
  { code: '+49', name: 'Germany', flag: 'ðŸ‡©ðŸ‡ª' },
  { code: '+33', name: 'France', flag: 'ðŸ‡«ðŸ‡·' },
  { code: '+34', name: 'Spain', flag: 'ðŸ‡ªðŸ‡¸' },
  { code: '+39', name: 'Italy', flag: 'ðŸ‡®ðŸ‡¹' },
  { code: '+81', name: 'Japan', flag: 'ðŸ‡¯ðŸ‡µ' },
  { code: '+86', name: 'China', flag: 'ðŸ‡¨ðŸ‡³' },
];

function CountrySelect({ value, onChange, className }) {
  const [open, setOpen] = useState(false);
  const [q, setQ] = useState('');
  const ref = useRef();

  useEffect(() => {
    function onDoc(e) {
      if (ref.current && !ref.current.contains(e.target)) setOpen(false);
    }
    document.addEventListener('click', onDoc);
    return () => document.removeEventListener('click', onDoc);
  }, []);

  const list = COUNTRIES.filter(c => (`${c.name} ${c.code}`).toLowerCase().includes(q.toLowerCase()));

  const current = COUNTRIES.find(c => c.code === value) || COUNTRIES[0];

  return (
    <div ref={ref} className={className} style={{ position: 'relative' }}>
      <button type="button" onClick={() => setOpen(s => !s)} className="country-btn">
        <span style={{ marginRight: 8 }}>{current.flag}</span>
        <span style={{ marginRight: 8 }}>{current.code}</span>
        <span style={{ opacity: 0.6 }}>{current.name}</span>
      </button>

      {open && (
        <div className="country-dropdown">
          <input className="country-search" placeholder="Search country or code" value={q} onChange={e => setQ(e.target.value)} />
          <ul className="country-list">
            {list.map(c => (
              <li key={c.code} className="country-item" onClick={() => { onChange(c.code); setOpen(false); }}>
                <span style={{ marginRight: 8 }}>{c.flag}</span>
                <strong style={{ marginRight: 8 }}>{c.code}</strong>
                <span style={{ color: '#333' }}>{c.name}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default CountrySelect;
