import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

export default function JobhunterDashboard() {
  const { profile } = useAuth();
  return (
    <div>
      {/* Header */}
      <header style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '14px 24px', borderBottom: '1px solid #eee', background: '#fff', position: 'sticky', top: 0, zIndex: 20 }}>
        <div style={{ fontWeight: 700, fontSize: 20 }}>Workconnect</div>
        <nav>
          <div style={{ display: 'flex', gap: 18, alignItems: 'center' }}>
            <Link to="/jobhunter/dashboard" style={{ color: '#052012', textDecoration: 'none', fontWeight: 600 }}>Home</Link>
            <Link to="/jobs" style={{ color: '#374151', textDecoration: 'none' }}>Jobs</Link>
            <Link to="/profile" style={{ color: '#374151', textDecoration: 'none' }}>Profile</Link>
          </div>
        </nav>
      </header>

      <main style={{ padding: 24 }}>
        <h1>Jobseeker Dashboard</h1>
        <p>Welcome{profile?.firstName ? `, ${profile.firstName}` : ''} — here are your recommended jobs.</p>
        {/* Placeholder content */}
        <div style={{ marginTop: 18 }}>
          <p><strong>Recommended Jobs</strong></p>
          <ul>
            <li>Frontend Developer — Remote — Acme</li>
            <li>Full-stack Engineer — New York — BetaCorp</li>
          </ul>
        </div>
      </main>
    </div>
  );
}
