import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useSignup } from "../../contexts/SignupContext";
import "./signup.css";
import { validatePhone } from "../../utils/phone";
import CountrySelect from "./CountrySelect";
import { BsEye, BsEyeSlash } from "react-icons/bs";

function SignUp01() {
  const { data, update, setCurrentStep } = useSignup();
  const navigate = useNavigate();

  const [phoneError, setPhoneError] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const onChange = (e) => update({ [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();

    const country = data.phoneCountry || "+63";
    const result = await validatePhone(country, data.phone || "");
    if (!result.valid) {
      setPhoneError("Invalid phone number for the selected country");
      return;
    }

    update({ phone: result.e164 });
    navigate("/signup-02");
  };

  useEffect(() => setCurrentStep(1), [setCurrentStep]);

  return (
    <div className="signup-page">
      {/* Left section with brand message */}
      <div className="signup-left">
        <h1 className="brand-title">WorkConnect</h1>
        <p className="brand-subtitle">
          Join a growing community of professionals and employers.
        </p>
      </div>

      {/* Right section (form) */}
      <div className="signup-right">
        <div className="signup-card">
          <h2>Create your account</h2>
          <p className="signup-desc">Itâ€™s quick and easy.</p>

          {/* Role selection */}
          <div className="role-toggle-signup">
            <label className={data.role === "jobhunter" ? "active" : ""}>
              <input
                type="radio"
                name="role"
                value="jobhunter"
                checked={data.role === "jobhunter"}
                onChange={(e) => update({ role: e.target.value })}
              />
              Job Hunter / Employee
            </label>
            <label className={data.role === "employer" ? "active" : ""}>
              <input
                type="radio"
                name="role"
                value="employer"
                checked={data.role === "employer"}
                onChange={(e) => update({ role: e.target.value })}
              />
              Employer
            </label>
          </div>

          {/* Form fields */}
          <form onSubmit={onSubmit}>
            <div className="form-grid">
              <div className="form-group">
                <label>First Name</label>
                <input
                  name="firstName"
                  value={data.firstName}
                  onChange={onChange}
                  placeholder="Enter your first name"
                  required
                />
              </div>

              <div className="form-group">
                <label>Last Name</label>
                <input
                  name="lastName"
                  value={data.lastName}
                  onChange={onChange}
                  placeholder="Enter your last name"
                  required
                />
              </div>

              <div className="form-group">
                <label>Email</label>
                <input
                  name="email"
                  value={data.email}
                  onChange={onChange}
                  placeholder="example@email.com"
                  required
                />
              </div>

              <div className="form-group">
                <label style={{ display: "flex", justifyContent: "space-between" }}>
                  <span>Password</span>
                  <span
                    className="eye-toggle-label"
                    onClick={() => setShowPassword(!showPassword)}
                    title={showPassword ? "Hide password" : "Show password"}
                  >
                    {showPassword ? <BsEyeSlash /> : <BsEye />}
                  </span>
                </label>
                <input
                  type={showPassword ? "text" : "password"}
                  name="password"
                  value={data.password}
                  onChange={onChange}
                  placeholder="Create a password"
                  required
                />
              </div>

              <div className="form-group">
                <label>Phone Number</label>
                <div className="phone-input">
                  <CountrySelect
                    value={data.phoneCountry || "+63"}
                    onChange={(code) => {
                      setPhoneError("");
                      update({ phoneCountry: code });
                    }}
                  />
                  <input
                    name="phone"
                    value={data.phone}
                    onChange={(e) => {
                      setPhoneError("");
                      onChange(e);
                    }}
                    placeholder="712345678"
                    required
                  />
                </div>
                {phoneError && <div className="signup-error">{phoneError}</div>}
              </div>

              <button type="submit" className="signup-btn">
                Continue
              </button>
            </div>
          </form>

          <p className="signin-link">
            Already have an account? <a href="/login">Sign in</a>
          </p>
        </div>
      </div>
    </div>
  );
}

export default SignUp01;
